          <BuyMeCoffeeButton
            href="https://www.buymeacoffee.com/devimun909"
            target="_blank"
            rel="noopener noreferrer"
          >
            <img
              src="https://cdn.buymeacoffee.com/buttons/bmc-new-btn-logo.svg"
              alt="Buy me a coffee"
              loading="lazy"
            />
            Í∞úÎ∞úÏûê ÌõÑÏõêÌïòÍ∏∞
          </BuyMeCoffeeButton>
        </ButtonsContainer>
        <Copyright>¬© {currentYear} SQLD Ïä§ÌÑ∞Îîî. All rights reserved.</Copyright>
      </FooterContent>
    </FooterContainer>
  );
};

export default Footer;
