import React from 'react';
import styled from 'styled-components';

const QuestionContainer = styled.div`
  width: 100%;
  padding: 20px;
  background-color: rgba(12, 20, 28, 0.5);
  border-radius: 12px;
  color: #d7dfe5;
  font-family: "IBM Plex Sans", sans-serif;

  @media (max-width: 768px) {
    padding: 16px;
  }

  @media (max-width: 480px) {
    padding: 12px;
  }
`;

const QuestionHeader = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 20px;
  padding-bottom: 10px;
  border-bottom: 1px solid rgba(214, 224, 230, 0.2);

  @media (max-width: 768px) {
    margin-bottom: 16px;
    padding-bottom: 8px;
  }
`;

const QuestionNumber = styled.h3`
  color: #d7dfe5;
  font-weight: 500;
  font-family: "IBM Plex Sans", sans-serif;

  @media (max-width: 768px) {
    font-size: 15px;
  }

  @media (max-width: 480px) {
    font-size: 14px;
  }
`;

const QuestionText = styled.h2`
  font-size: 1.3rem;
  font-weight: 600;
  margin-bottom: 20px;
  line-height: 1.5;
  font-family: "IBM Plex Sans", sans-serif;

  @media (max-width: 768px) {
    font-size: 1.1rem;
    margin-bottom: 16px;
  }

  @media (max-width: 480px) {
    font-size: 1rem;
    margin-bottom: 14px;
  }
`;

const DiagramContainer = styled.div`
  margin: 20px 0;
  padding: 15px;
  background-color: rgba(215, 223, 228, 0.08);
  border-radius: 12px;
  overflow-x: auto;

  @media (max-width: 768px) {
    margin: 16px 0;
    padding: 12px;
  }

  @media (max-width: 480px) {
    margin: 14px 0;
    padding: 10px;
  }
`;

const TableDisplay = styled.table`
  width: 100%;
  border-collapse: collapse;
  margin: 15px 0;
  font-size: 0.9em;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 0 20px rgba(0, 0, 0, 0.15);

  @media (max-width: 768px) {
    font-size: 0.85em;
    margin: 12px 0;
  }

  @media (max-width: 480px) {
    font-size: 0.8em;
    margin: 10px 0;
  }
`;

const TableRow = styled.tr`
  background-color: ${props => props.isHeader ? '#1e2832' : '#0d151e'};
  border-bottom: 1px solid rgba(214, 224, 230, 0.1);

  &:last-child {
    border-bottom: 2px solid #2395e9;
  }

  &:hover {
    background-color: ${props => props.isHeader ? '#1e2832' : '#151e28'};
  }
`;

const TableCell = styled.td`
  padding: 12px 15px;
  text-align: left;
  font-family: "IBM Plex Sans", sans-serif;

  @media (max-width: 768px) {
    padding: 10px 12px;
  }

  @media (max-width: 480px) {
    padding: 8px 10px;
  }
`;

const TableHeader = styled.th`
  padding: 12px 15px;
  text-align: left;
  color: #d7dfe5;
  font-weight: 600;
  font-family: "IBM Plex Sans", sans-serif;

  @media (max-width: 768px) {
    padding: 10px 12px;
  }

  @media (max-width: 480px) {
    padding: 8px 10px;
  }
`;

const OptionsContainer = styled.div`
  margin-top: 30px;

  @media (max-width: 768px) {
    margin-top: 24px;
  }

  @media (max-width: 480px) {
    margin-top: 20px;
  }
`;

const OptionItem = styled.div`
  padding: 12px 15px;
  margin-bottom: 10px;
  background-color: ${props => 
    props.isSelected 
      ? 'rgba(35, 149, 233, 0.4)'
      : props.isCorrect && props.showAnswer
        ? 'rgba(60, 179, 113, 0.4)'
        : props.isWrong && props.showAnswer
          ? 'rgba(255, 77, 77, 0.4)'
          : 'rgba(215, 223, 228, 0.08)'
  };
  border-radius: 12px;
  cursor: ${props => props.disabled ? 'default' : 'pointer'};
  transition: background-color 0.2s;
  display: flex;
  align-items: center;
  font-family: "IBM Plex Sans", sans-serif;

  &:hover {
    background-color: ${props => 
      props.disabled 
        ? props.isSelected 
          ? 'rgba(35, 149, 233, 0.4)'
          : props.isCorrect && props.showAnswer
            ? 'rgba(60, 179, 113, 0.4)'
            : props.isWrong && props.showAnswer
              ? 'rgba(255, 77, 77, 0.4)'
              : 'rgba(215, 223, 228, 0.08)'
        : 'rgba(35, 149, 233, 0.2)'
    };
  }

